import 'package:carousel_slider/carousel_slider.dart';
import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:sliding_up_panel/sliding_up_panel.dart';
import 'package:univfinder/model/schools.dart';
import 'package:univfinder/utils/colors.dart';

class SingleSchool extends StatelessWidget {
  final Schools schools;
  const SingleSchool(this.schools, {Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return DefaultTabController(
      length: 3,
      child: Scaffold(
        body: SlidingUpPanel(
          maxHeight: MediaQuery.of(context).size.height / 1.4,
          backdropOpacity: 0.3,
          backdropEnabled: true,
          parallaxEnabled: true,
          parallaxOffset: 0.4,
          onPanelOpened: () {},
          body: _body(context),
          panel: _panel(),
          collapsed: const Align(
            alignment: Alignment.topCenter,
            child: Icon(
              Icons.drag_handle,
              size: 35,
            ),
          ),
          borderRadius: const BorderRadius.only(
              topLeft: Radius.circular(15), topRight: Radius.circular(15)),
        ),
      ),
    );
  }

  Widget _body(context) {
    return Stack(
      children: [
        Container(
          decoration: BoxDecoration(
            image: DecorationImage(
                image: AssetImage(schools.img), fit: BoxFit.cover),
            borderRadius: BorderRadius.circular(15),
          ),
          child: Container(
            decoration: BoxDecoration(
              borderRadius: BorderRadius.circular(15),
              gradient: LinearGradient(
                begin: Alignment.bottomCenter,
                colors: [
                  blackColor.withOpacity(.4),
                  blackColor.withOpacity(.3),
                  blackColor.withOpacity(.2),
                ],
              ),
            ),
          ),
        ),
        Positioned(
          top: 50,
          left: 25,
          child: Container(
            decoration: BoxDecoration(
              color: whiteColor.withOpacity(0.5),
              borderRadius: BorderRadius.circular(30),
            ),
            child: Padding(
              padding: const EdgeInsets.all(8.0),
              child: InkWell(
                onTap: () {
                  Navigator.pop(context);
                },
                child: const Icon(Icons.arrow_back),
              ),
            ),
          ),
        ),
      ],
    );
  }

  Widget _panel() {
    return Column(
      children: [
        const SizedBox(
          height: 35,
        ),
        Text(
          schools.schoolName,
          style: const TextStyle(fontSize: 20, fontWeight: FontWeight.bold),
        ),
        Row(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            const Icon(Icons.location_on),
            Text(schools.location),
          ],
        ),
        const SizedBox(
          height: 25,
        ),
        Expanded(
          child: ListView(
            shrinkWrap: true,
            children: [
              gallery(),
              const SizedBox(
                height: 10,
              ),
              about(),
              tabsBody(),
            ],
          ),
        ),
      ],
    );
  }

  Widget gallery() {
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          const Text(
            'Gallery',
            style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600),
          ),
          const SizedBox(
            height: 10,
          ),
          CarouselSlider.builder(
            options: CarouselOptions(
              enableInfiniteScroll: false,
              autoPlay: false,
              enlargeCenterPage: true,
              viewportFraction: 0.5,
              aspectRatio: 1.5,
              initialPage: 1,
            ),
            itemCount: schools.gallery.length,
            itemBuilder:
                (BuildContext context, int itemIndex, int pageViewIndex) =>
                    Container(
              margin: const EdgeInsets.symmetric(horizontal: 10),
              child: GestureDetector(
                onTap: () {
                  Navigator.pushNamed(context, 'imageViewer',
                      arguments: schools.gallery[itemIndex]);
                },
                child: Image.asset(schools.gallery[itemIndex],
                    height: 200, width: 200, fit: BoxFit.cover),
              ),
            ),
          ),
        ],
      ),
    );
  }

